import axios from 'axios';
import { useNavigate, useParams } from "react-router-dom";
import { Cabecera } from "../cabecera/cabecera";
import React, { useState, useEffect } from 'react';

export const AreaPersonal = () => {

    const { idUsuario } = useParams()
    const URIrecetas = "http://localhost:8000/recetas/"
    const URIrecetasUsuario="http://localhost:8000/recetasusuarios/"
    const URIUsuario="http://localhost:8000/usuarios/"
    const [recipes, setRecipes] = useState([]);
    const [recetasU, setRecetasU]= useState([])
    const [usuario,setUsuario] = useState({})
    const navigate = useNavigate();

    useEffect(() => {
        getRecetaByID()

    }, [])


    const getRecetaByID = async () => {
        try {

            const res = await axios.get(URIrecetasUsuario + idUsuario);
            setRecetasU(res.data);

            const promises = res.data.map(async (id) => {
                const res2 = await axios.get(URIrecetas + id.idReceta);
                return res2.data;
            });

            const recipesData = await Promise.all(promises);
            setRecipes(recipesData);
        } catch (error) {
            console.error(error);
        }
        const res3 = await axios.get(URIUsuario + idUsuario)
        setUsuario(res3.data)
    };

    const crearReceta = () =>{
        navigate("/crearReceta");
    }

    return (
        <div>
            <Cabecera />
            <div className='parteDeArriba'>
                        <h1 className='medioUsuarios'>Bienvenido {usuario.nombre}</h1>
                        </div>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12 mb-4">
                        <div className="row">
                        <h3 className='h3Usuarios'>Estas son tus recetas guardadas</h3>
                        {recipes.map(recipe => (
                                <div className="col-sm-3" key={recipe.id}>
                                    <div className="card">
                                        <img className="card-img-top" src={recipe.Image_Name} alt={recipe.title} style={{ objectFit: 'cover', height: '200px' }} />
                                        <div className="card-body">
                                            <h5 className="card-title">{recipe.Tittle}</h5>
                                            <div>
                                                <a href={`/recetaExtendida/${recipe.id}`} className="btn btn-outline-success"> Receta completa</a>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
            <button onClick={crearReceta} className="buttonEdit bg-body-tertiary">Crear Receta</button>
        </div>
    )
}
