import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { Cabecera } from "../cabecera/cabecera";
import React, { useState, useEffect } from 'react';


export const Explorador = () => {
    const [comensales, setComensales] = useState(0);
    const [alimentos, setAlimentos] = useState([]);
    const [tiempo, setTiempo] = useState("");
    const URIrecetas = "http://localhost:8000/recetas/"
    const URIrecetasusuarios = "http://localhost:8000/recetasusuarios"
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        fetch(URIrecetas)
            .then(response => response.json())
            .then(data => setRecipes(data))
            .catch(error => console.error(error));
    }, []);
    console.log(localStorage.getItem('user'))
    const [busqueda, setBusqueda] = useState('');

    const buscarRecetas = async (e) => {
        e.preventDefault()
        console.log(busqueda)
        let url = URIrecetas
        if (busqueda) {
            url = `http://localhost:8000/recetas/search/${busqueda}`;
        }

        try {
          const url = busqueda ? `http://localhost:8000/recetas/search/${busqueda}` : URIrecetas;
          if (comensales > 0) {
            url += `?comensales=${comensales}`
        }
        if (tiempo) {
            url += `&tiempo=${tiempo}`
        }
          const response = await fetch(url);
          const recetas = await response.json();
          setRecipes(recetas);
        } catch (error) {
          console.error("Algo falló " + error);
        }
      }

    const guardarReceta = async (id, e) => {
        e.preventDefault()
        try{
            const idReceta=id;
            const idUsuario=localStorage.getItem('user');
            const receta = await axios.get(`${URIrecetasusuarios}/idReceta/${idReceta}/idUsuario/${idUsuario}`);


            if (receta.data==null) {
                console.log("a")
                const result = axios.post(URIrecetasusuarios,{
                    idReceta: id,
                    idUsuario:localStorage.getItem('user')
                })
            }
        }catch (error){
            console.error("Algo falló " + error);
        }
    }

    const limpiarSeleccion = async (e) => {
        try{
            const busqueda = document.getElementById("busqueda")
            setBusqueda("");


        }catch (error){
            console.error("Fallo al limpiar selección");
        }
      }


      console.log(recipe)

    return (
        <div>
            <Cabecera />

            <h1 className="tituloBuscador">RecetApp</h1>
            <p className="pBuscador ">Descubre todo nuestro recetario</p>
            <form className="busquedaForm" onSubmit={buscarRecetas}>
                <input value={busqueda} className="buscador" onChange={(e) => setBusqueda(e.target.value)} type="text" placeholder="Ej:Fajitas mexicanas" id="busqueda" name="busqueda"  />
                <button className="btn btn-success btn-sm busquedaReceta" type="submit">Buscar</button>
                <button onClick={limpiarSeleccion} className="btn btn-success btn-sm busquedaLimpiar">Limpiar</button>
             </form>
            <div className="container">
                <div className="row">
                    <div className="sticky-top col-sm-3">
                        <div className="card">
                        <h5 className="card-title">Filtrar por Alimentos</h5>
                        </div>
                        <div className="card">
                        <h5 className="card-title">Filtrar por Tiempo</h5>
                        <select className="form-control" value={tiempo} onChange={(e) => setTiempo(e.target.value)}>
                            <option value="">Todos</option>
                            <option value="menos-de-30">Menos de 30 minutos</option>
                            <option value="entre-30-y-60">Entre 30 y 60 minutos</option>
                            <option value="mas-de-60">Más de 60 minutos</option>
                        </select>
                        </div>  
                        <div className="card">
                        <h5 className="card-title">Filtrar por Comensales</h5>
                        <select className="form-control" value={comensales} onChange={(e) => setComensales(parseInt(e.target.value))}>
                                <option value={0}>Todos</option>
                                <option value={1}>1 comensal</option>
                                <option value={2}>2 comensales</option>
                                <option value={3}>3 comensales</option>
                                <option value={4}>4 comensales</option>
                                <option value={5}>5+ comensales</option>
                        </select>
                        </div>      
                    </div>
                    <div className="col-sm-8 mb-4">
                        <div className="row">
                        {recipes.filter(recipe => 
                        (comensales == 0 || recipe.comensales == comensales)&&
                        (tiempo === "" || 
                        (tiempo === "menos-de-30" && recipe.tiempo_preparacion < 30) ||
                        (tiempo === "entre-30-y-60" && recipe.tiempo_preparacion >= 30 && recipe.tiempo_preparacion <= 60) ||
                        (tiempo === "mas-de-60" && recipe.tiempo_preparacion > 60))
                        ).map(recipe => (
                                <div className="col-sm-4" key={recipe.id}>
                                    <div className="card">
                                        <img className="card-img-top" src={recipe.Image_Name} alt={recipe.title} style={{ objectFit: 'cover', height: '200px' }} />
                                        <div className="card-body">
                                            <h5 className="card-title">{recipe.Tittle}</h5>
                                            <div>
                                                <a href={`./recetaExtendida/${recipe.id}`} className="btn btn-outline-success"> Receta completa</a>
                                                <button onClick={guardarReceta.bind(this, recipe.id)} className="btn btn-outline-success" type="submit">Guardar</button> 
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>

    )
}
